import React, { useState, useEffect } from 'react'
import axios from 'axios'

export default function UserPage(props) {
  const initialUserState = {
    user: {},
    loading: true,
  }

  const [user, setUser] = useState(initialUserState)

  useEffect(() => {
    const getUser = async () => {
      //const { data } = await axios(`https://api.github.com/users/${props.match.params.id}`)
      const { data } = await axios(`https://api.redonemedia.co.uk/_/items/property?filter[id]=${props.match.params.id}`)

      setUser(data)
    }

    getUser()
  }, [])

  return user.loading ? (
    <div>Loading...</div>
  ) : (
    <div className="container">
      <h1>{props.match.params.id}</h1>

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Location</th>
            <th>Website</th>
            <th>Followers</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{props.match.params.id.house_number}</td>
            <td>{user.location}</td>
            <td>
              <a href={user.blog}>{user.blog}</a>
            </td>
            <td>{user.followers}</td>
          </tr>
        </tbody>
      </table>
    </div>
  )
}
